import React from 'react';
import './TicketCard.css';

const TicketCard = ({ ticket, users }) => {
  const [showMore, setShowMore] = useState(false); // State to toggle more info
  const priorityLabels = ['No Priority', 'Low', 'Medium', 'High', 'Urgent'];

  // Get the user's name from their ID
  const assignedUser = users.find((user) => user.id === ticket.userId)?.name || 'Unassigned';

  return (
    <div className="ticket-card">
      <h4>{ticket.title}</h4>
      <p><strong>Assigned To:</strong> {assignedUser}</p>
      <p><strong>Status:</strong> {ticket.status}</p>
      <p><strong>Priority:</strong> {priorityLabels[ticket.priority]}</p>

      {/* Three Dots for More Info */}
      <div className="more-info">
        <button onClick={() => setShowMore(!showMore)} className="three-dots">
          ...
        </button>
        {showMore && (
          <div className="extra-info">
            <p><strong>Tags:</strong> {ticket.tag.join(', ')}</p>
            <p><strong>ID:</strong> {ticket.id}</p>
          </div>
        )}
      </div>
    </div>
  );
};
export default TicketCard;
